package com.syntax.class09;

public class MoreNestedLoopTables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//create multiplication table
		for (int x=1; x<=10; x++) {
			for (int y=1; y<=10; y++) {
				System.out.println(x+" x "+y+" = "+(x*y));
				}
			System.out.println("---------------");
		}
	}

}
